import React, { Component } from "react";

console.log(this.props.user);

class Info extends Component {
  render() {
    return (
      <React.Fragment>
        <p>{this.props.user.location}</p>
        <h3 className="user-deets">
          <a href={this.props.user.twitterUsername}>{this.props.user.name}</a>
        </h3>
        <div className="user-deets">Location</div>
        <div className="user-deets">{this.props.user.location}</div>
      </React.Fragment>
    );
  }
}

export default Info;
