import React, { Component } from "react";

class Info extends Component {
  render() {
    const { location, foodType, age, likes } = this.props.user;
    const twitterLink =
      "https://twitter.com/" + this.props.user.twitterUsername;

    return (
      <React.Fragment>
        <h3 className="user-deets">
          <a href={twitterLink}>{this.props.user.name}</a>
        </h3>
        <div className="user-deets">
          <strong>Location</strong>
        </div>
        <div className="user-deets">{location}</div>
        <div className="user-deets">
          <strong>Eats</strong>
        </div>
        <div className="user-deets">{foodType}</div>
        <div className="user-deets">
          <strong>Age</strong>
        </div>
        <div className="user-deets">{age}</div>
        <div className="user-deets">
          <strong>Likes</strong>
        </div>
        <div className="user-deets">{likes}</div>
        <div className="user-deets">
          <strong>Twitter</strong>
        </div>
        <div className="user-deets">
          <a href={twitterLink}>@{this.props.user.twitterUsername}</a>
        </div>
      </React.Fragment>
    );
  }
}

export default Info;
