import React, { Component } from "react";

class Info extends Component {
  render() {
    return (
      <div>
        <h3 className="user-deets">
          <a href="{ 'https://twitter.com/' + this.state.twitterUsername  }">
            {this.state.user.name}
          </a>
        </h3>
        <div className="user-deets">Location</div>
        <div className="user-deets">{this.state.user.location}</div>
      </div>
    );
  }
}

export default Info;
