import React, { useState } from "react";
import ReactDOM from "react-dom";
import "./styles.css";

function App() {
  const [position, setPosition] = useState(300);
  console.log(position);

  let boxStyle = `transform: translateY(${position}px);`;
  console.log(boxStyle);

  return (
    <div className="App">
      <h1>Move the Box!</h1>

      {/* handle the click event on this button */}
      <button onClick={e => setPosition(position - 5)}>ðŸ‘† Move Up ðŸ‘†</button>

      {/* move this box using inline styles */}
      <div className="box" style={{ transform: `translateY(${position}px)` }} />
    </div>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
