  constructor() {
    super();
    this.handleFetch = this.handleFetch.bind(this);
    this.state = {
      apiURL: "https://www.anapioficeandfire.com/api/books?pageSize=30",
      apiData: []
    };
  }

  handleFetch() {
    this.setState({
      apiData: 0
    });
    fetch(this.state.apiURL)
      .then(i => i.json())
      .then(i => this.state.apiData.push(...i));
    console.log(this.state.apiData);
  }

