import React, { Component } from "react";
import ReactDOM from "react-dom";
import ScotchInfoBar from "./ScotchInfoBar";
import "./styles.css";

class App extends Component {
  constructor() {
    super();
    this.handleFetch = this.handleFetch.bind(this);
    this.state = {
      apiURL: "https://www.anapioficeandfire.com/api/books?pageSize=30",
      apiData: []
    };
  }

  handleFetch() {
    this.setState({
      apiData: 0
    });
    fetch(this.state.apiURL)
      .then(i => i.json())
      .then(i => this.state.apiData.push(...i));
    console.log(this.state.apiData);
  }

  render() {
    const bookBox = this.state.apiData.map((book, index) => (
      <div className="books">
        <div className="book">
          <h3>Book {index + 1}</h3>
          <h2>{book.name}</h2>

          <div className="details">
            <p>üë®: book.authors</p>
            <p>üìñ: book.numberOfPages</p>
            <p>üèòÔ∏è: book.country</p>
            <p>‚è∞: book.released</p>
          </div>
        </div>
      </div>
    ));

    return (
      <div className="App">
        <h1>Game of Thrones Books</h1>
        <h2>Fetch a list from an API and display it</h2>

        {/* Fetch data from API */}
        <div>
          <button className="fetch-button" onClick={this.handleFetch}>
            Fetch Data
          </button>
          <br />
        </div>

        {/* Display data from API */}

        {/* Use JSX below for each book */}
        {}

        <ScotchInfoBar seriesNumber="7" />
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
